/**
 * Numerals string helper
 */
function numeralsString(num, s1, s2, s3) {
    var mod = num % 10;

    if (num === undefined) {
        return [];
    }

    return [num, mod == 1 ? s1 : mod > 1 && mod < 5 ? s2 : s3];
}
